import methods from 'http-methods-enum'
import { CREATED } from 'http-status-codes'
import schema from '../../schema'
export default ({ services: { repositories: { thing } } }) => {
	return {
		path: '/',
		schema,
		method: methods.POST,
		handler: async (ctx) => {
			ctx.response.body = await thing.create(ctx.request.body)
			ctx.status = CREATED
		}
	}
}
