import methods from 'http-methods-enum'
import { OK } from 'http-status-codes'
export default ({ services: { repositories: { thing } } }) => {
	return {
		path: '/:id',
		method: methods.GET,
		handler: async (ctx) => {
			const { id } = ctx.params.id
			const things = await thing.find({ id })
			ctx.body = things
			ctx.status = OK
		}
	}
}
