import methods from 'http-methods-enum'
import { OK } from 'http-status-codes'
export default ({ services: { repositories: { thing } } }) => {
	return {
		path: '/',
		method: methods.GET,
		handler: async (ctx) => {
			const things = await thing.find(ctx.query)
			ctx.body = things
			ctx.status = OK
		}
	}
}
