const isDeployed = () => process.env.DEPLOYED === 'true' || process.env.DEPLOYED === true;

export default async (userConfig) => {
	return {
		dev: !isDeployed(),
		server: {
			port: process.env.PORT || 3000
		},
		db: {
			url: 'localhost:27017',
			name: 'bmo',
			collections: {
				things: 'things'
			},
			useAuth: false,
			username: '',
			password: ''
		},
		routes: {
			things: {
				v1: '/api/things/v1'
			}
		},
		...userConfig
	};
};
