import uuid from 'uuid';
const collection = 'things';
export default ({ dependencies: { database } }) => ({
	create: async (model) => {
		// Do validation and other stuff here
		return await database[collection].create(model);
	},
	update: async (model) => {
		return await database[collection].update(model);
	},
	find: async (query) => {
		return await database[collection].find(query);
	}
});
