import methods from 'http-methods-enum';
import { OK } from 'http-status-codes';
export default ({ dependencies: { repositories: { thing } }, routes: { things } }) => {
	return {
		path: `${things.v1}/:id`,
		method: methods.PUT,
		handler: async (ctx) => {
			const { id } = ctx.params.id;
			const things = await thing.update(ctx.body);
			ctx.body = things;
			ctx.status = OK;
		}
	};
};
